mapper.select:
SELECT patient_id, patient_number, name, birth_date, phone, password
FROM patients
WHERE patient_id = #{patientId}

SELECT patient_id, patient_number, name, birth_date, phone, password
FROM patients
WHERE phone = #{phoneNumber}

mapper.update:
UPDATE patients
SET name = #{name},
    birth_date = #{birthDate},
    phone = #{phoneNumber}
WHERE patient_id = #{patientId}

UPDATE patients
SET password = #{password}
WHERE patient_id = #{patientId}
