mapper.select:
SELECT patient_id, name, birth_date, phone_number, password, reset_token, reset_token_expiry
FROM patients
WHERE patient_id = #{patientId}

SELECT patient_id, name, birth_date, phone_number, password, reset_token, reset_token_expiry
FROM patients
WHERE phone_number = #{phoneNumber}

mapper.update:
UPDATE patients
SET name = #{name},
    birth_date = #{birthDate},
    phone_number = #{phoneNumber}
WHERE patient_id = #{patientId}

UPDATE patients
SET password = #{password}
WHERE patient_id = #{patientId}
